// src/components/Vegetables.jsx
import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchProducts } from "../redux/ProductSlice/ProductsSlice"; // Adjust import path as needed
import Cart from "./Cart"; // Import the Cart component

function Vegetables() {
  const dispatch = useDispatch();
  const { products, status, error } = useSelector((state) => state.products);

  useEffect(() => {
    dispatch(fetchProducts());
  }, [dispatch]);

  return (
    <div className="container mx-auto px-6 py-12">
      <h1 className="text-4xl font-semibold text-center text-gray-800 mb-8">
        Fresh Vegetables
      </h1>

      {/* Handle loading, error, or success states */}
      {status === "loading" && (
        <p className="text-center text-gray-500">Loading...</p>
      )}
      {status === "failed" && (
        <p className="text-center text-red-500">{error}</p>
      )}

      {/* Pass the fetched products to the Cart component */}
      {status === "succeeded" && <Cart products={products} />}
      {status === "succeeded" && products.length === 0 && (
        <p className="text-center text-gray-500">No products found</p>
      )}
    </div>
  );
}

export default Vegetables;
