import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  fetchProductsAPI1,
  fetchProductsAPI2,
} from "../../ProductSlice/smartStoreSlice"; // Adjust the import path as necessary

const SmartStoreOverview = () => {
  const dispatch = useDispatch();
  const { itemsAPI1, itemsAPI2, statusAPI1, statusAPI2, errorAPI1, errorAPI2 } =
    useSelector((state) => state.smartStore);

  useEffect(() => {
    dispatch(fetchProductsAPI1());
    dispatch(fetchProductsAPI2());
  }, [dispatch]);

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-3xl font-semibold text-center text-gray-800 mb-8">
        Smart Store Overview
      </h1>

      {/* API1 Products Section */}
      <div className="bg-white shadow-lg rounded-lg p-6 mb-6">
        <h2 className="text-2xl font-medium text-gray-700 mb-4">
          API1 Products
        </h2>
        {statusAPI1 === "loading" && (
          <p className="text-center text-gray-500">Loading...</p>
        )}
        {statusAPI1 === "failed" && (
          <p className="text-center text-red-500">{errorAPI1}</p>
        )}
        {statusAPI1 === "succeeded" && (
          <ul className="space-y-4">
            {Array.isArray(itemsAPI1) && itemsAPI1.length > 0 ? (
              itemsAPI1.map((product) => (
                <li
                  key={product.id}
                  className="flex items-center justify-between p-4 bg-gray-50 rounded-lg shadow-md hover:bg-gray-100 transition duration-300"
                >
                  <span className="text-lg text-gray-800">{product.name}</span>
                </li>
              ))
            ) : (
              <p className="text-center text-gray-500">No products found</p>
            )}
          </ul>
        )}
      </div>

      {/* API2 Products Section */}
      <div className="bg-white shadow-lg rounded-lg p-6">
        <h2 className="text-2xl font-medium text-gray-700 mb-4">
          API2 Products
        </h2>
        {statusAPI2 === "loading" && (
          <p className="text-center text-gray-500">Loading...</p>
        )}
        {statusAPI2 === "failed" && (
          <p className="text-center text-red-500">{errorAPI2}</p>
        )}
        {statusAPI2 === "succeeded" && (
          <ul className="space-y-4">
            {Array.isArray(itemsAPI2) && itemsAPI2.length > 0 ? (
              itemsAPI2.map((product) => (
                <li
                  key={product.id}
                  className="flex items-center justify-between p-4 bg-gray-50 rounded-lg shadow-md hover:bg-gray-100 transition duration-300"
                >
                  <span className="text-lg text-gray-800">{product.name}</span>
                </li>
              ))
            ) : (
              <p className="text-center text-gray-500">No products found</p>
            )}
          </ul>
        )}
      </div>
    </div>
  );
};

export default SmartStoreOverview;
