import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchProductsAPI1, fetchProductsAPI2 } from "./smartStoreSlice"; // Adjust import path if needed

const SmartStoreOverview = () => {
  const dispatch = useDispatch();

  // Accessing data from Redux store
  const { itemsAPI1, itemsAPI2, statusAPI1, statusAPI2 } = useSelector(
    (state) => state.smartStore
  );

  useEffect(() => {
    // Dispatch actions to fetch data
    dispatch(fetchProductsAPI1());
    dispatch(fetchProductsAPI2());
  }, [dispatch]);

  // Log data for debugging
  useEffect(() => {
    console.log("API1 items:", itemsAPI1);
    console.log("API2 items:", itemsAPI2);
  }, [itemsAPI1, itemsAPI2]);

  return (
    <div className="p-6">
      <h2 className="text-xl font-bold mb-4">Products from API 1</h2>
      {statusAPI1 === "loading" ? (
        <p>Loading products from API 1...</p>
      ) : statusAPI1 === "failed" ? (
        <p>Error loading products from API 1</p>
      ) : (
        <ul>
          {itemsAPI1.length > 0 ? (
            itemsAPI1.map((product) => (
              <li
                key={product.product_id}
                className="flex items-center justify-between p-4 bg-gray-50 rounded-lg shadow-md hover:bg-gray-100 transition duration-300"
              >
                <img
                  src={`http://localhost:8000${product.image}`} // Ensure the image URL is correct
                  alt={product.name}
                  className="w-16 h-16 object-cover rounded-md mr-4"
                />
                <span className="text-lg text-gray-800">{product.name}</span>
              </li>
            ))
          ) : (
            <p>No products found from API 1</p>
          )}
        </ul>
      )}

      <h2 className="text-xl font-bold mt-8 mb-4">Products from API 2</h2>
      {statusAPI2 === "loading" ? (
        <p>Loading products from API 2...</p>
      ) : statusAPI2 === "failed" ? (
        <p>Error loading products from API 2</p>
      ) : (
        <ul>
          {itemsAPI2.length > 0 ? (
            itemsAPI2.map((product) => (
              <li
                key={product.product_id}
                className="flex items-center justify-between p-4 bg-gray-50 rounded-lg shadow-md hover:bg-gray-100 transition duration-300"
              >
                <img
                  src={`http://localhost:8000${product.image}`} // Ensure the image URL is correct
                  alt={product.name}
                  className="w-16 h-16 object-cover rounded-md mr-4"
                />
                <span className="text-lg text-gray-800">{product.name}</span>
              </li>
            ))
          ) : (
            <p>No products found from API 2</p>
          )}
        </ul>
      )}
    </div>
  );
};

export default SmartStoreOverview;
