import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { fetchProductsAPI1, fetchProductsAPI2 } from "./SmartStoreSlice";
import Loader from "./common/Loader";

function SmartStoreOverview() {
  const dispatch = useDispatch();

  const { itemsAPI1, itemsAPI2, statusAPI1, statusAPI2, errorAPI1, errorAPI2 } =
    useSelector((state) => state.smartStore);

  useEffect(() => {
    if (statusAPI1 === "idle") {
      dispatch(fetchProductsAPI1());
    }
    if (statusAPI2 === "idle") {
      dispatch(fetchProductsAPI2());
    }
  }, [statusAPI1, statusAPI2, dispatch]);

  if (statusAPI1 === "loading" || statusAPI2 === "loading") return <Loader />;

  if (statusAPI1 === "failed" || statusAPI2 === "failed") {
    return (
      <div className="text-red-500 text-center mt-4">
        Error: {errorAPI1 || errorAPI2 || "Failed to fetch products"}
      </div>
    );
  }

  return (
    <section className="p-6 bg-gray-100 dark:bg-gray-900">
      <h1 className="text-3xl font-bold mb-4 text-center text-gray-800 dark:text-gray-200">
        Smart Store Overview
      </h1>

      <h2 className="text-2xl font-semibold mt-6 text-gray-800 dark:text-gray-200">
        Products from API 1
      </h2>
      <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {itemsAPI1?.map((product) => (
          <div
            key={product.id}
            className="bg-white dark:bg-gray-800 shadow-md rounded-lg p-4 hover:shadow-lg transition duration-300"
          >
            <img
              src={product.image}
              alt={product.name}
              className="w-full h-48 object-cover rounded-md"
            />
            <h2 className="text-xl font-semibold mt-4 text-gray-800 dark:text-gray-100">
              {product.name}
            </h2>
            <p className="text-gray-600 dark:text-gray-400">
              {product.description}
            </p>
            <p className="text-green-600 font-bold mt-2">${product.price}</p>
            <button className="mt-4 bg-green-500 text-white px-4 py-2 rounded-full hover:bg-green-600 transition">
              Add to Cart
            </button>
          </div>
        ))}
      </div>

      <h2 className="text-2xl font-semibold mt-6 text-gray-800 dark:text-gray-200">
        Products from API 2
      </h2>
      <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {itemsAPI2?.map((product) => (
          <div
            key={product.id}
            className="bg-white dark:bg-gray-800 shadow-md rounded-lg p-4 hover:shadow-lg transition duration-300"
          >
            <img
              src={product.image}
              alt={product.name}
              className="w-full h-48 object-cover rounded-md"
            />
            <h2 className="text-xl font-semibold mt-4 text-gray-800 dark:text-gray-100">
              {product.name}
            </h2>
            <p className="text-gray-600 dark:text-gray-400">
              {product.description}
            </p>
            <p className="text-green-600 font-bold mt-2">${product.price}</p>
            <button className="mt-4 bg-green-500 text-white px-4 py-2 rounded-full hover:bg-green-600 transition">
              Add to Cart
            </button>
          </div>
        ))}
      </div>
    </section>
  );
}

export default SmartStoreOverview;
