import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

// Fetch products from API
export const fetchProducts = createAsyncThunk(
  "seed/fetchProducts",
  async () => {
    try {
      const response = await axios.get(
        "http://localhost:8000/api/products/seeds/"
      );
      console.log("API Response:", response.data); // Debugging API response
      return response.data; // Adjust to match your API response structure
    } catch (error) {
      throw new Error("Failed to fetch products");
    }
  }
);

const SeedSlice = createSlice({
  name: "seed",
  initialState: {
    products: [], // Array to store fetched products
    status: "idle", // idle | loading | succeeded | failed
    error: null, // For handling errors
  },
  extraReducers: (builder) => {
    builder
      .addCase(fetchProducts.pending, (state) => {
        state.status = "loading";
      })
      .addCase(fetchProducts.fulfilled, (state, action) => {
        state.status = "succeeded";
        state.products = action.payload.products || action.payload; // Adjust based on API response structure
      })
      .addCase(fetchProducts.rejected, (state, action) => {
        state.status = "failed";
        state.error = action.error.message;
      });
  },
});

export default SeedSlice.reducer;
