import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchProducts } from "../../ProductSlice/vegetableSlice";
import vege2 from "../assets/vege2.jpg";
import vege3 from "..//../assets/califlower.jpg";
import vege4 from "..//../assets/potato.jpg";
import vege5 from "..//..//assets/Masalas.jpg";
import vege6 from "..//../assets/babegi.jpg";

function Vegetables() {
  const dispatch = useDispatch();

  // Accessing vegetable products from the correct state (vegetable)
  const { products, status, error } = useSelector((state) => state.vegetable);

  useEffect(() => {
    dispatch(fetchProducts());
  }, [dispatch]);

  console.log(products); // Debugging

  // Map product names to images
  const imageMapping = {
    Anil: vege2,
    Sudheer: vege3,
    Mashrum: vege4,
    "Canli flower": vege5,
    Potato: vege6,
    cabagi: vege6, // Use an appropriate image for 'cabagi'
  };

  return (
    <div className="container mx-auto px-6 py-12">
      <h1 className="text-4xl font-semibold text-center text-gray-800 mb-8">
        Fresh Vegetables
      </h1>

      {/* Handle loading, error, or success states */}
      {status === "loading" && (
        <p className="text-center text-gray-500">Loading...</p>
      )}
      {status === "failed" && (
        <p className="text-center text-red-500">{error}</p>
      )}

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {/* Render the products if loading is done and products are available */}
        {status === "succeeded" && products.length > 0 ? (
          products.map((product) => {
            // Use the image from imageMapping or default to the image from API response
            const image =
              imageMapping[product.name] ||
              `http://localhost:8000${product.image}`;

            return (
              <div
                key={product.product_id}
                className="bg-white border border-gray-200 rounded-lg shadow-lg overflow-hidden"
              >
                <img
                  src={image} // Use the mapped image or fallback to API image URL
                  alt={product.name}
                  className="w-full h-64 object-cover"
                />
                <div className="p-4">
                  <h3 className="text-xl font-semibold text-gray-800">
                    {product.name}
                  </h3>
                  <p className="text-lg text-gray-600">
                    {product.product_info}
                  </p>
                  <p className="text-2xl font-bold text-gray-800">{`$${product.price}`}</p>
                  <button className="mt-4 bg-blue-500 text-white py-2 px-4 rounded-lg hover:bg-blue-600 transition">
                    Add to Cart
                  </button>
                </div>
              </div>
            );
          })
        ) : (
          <p className="text-center text-gray-500">No products found</p>
        )}
      </div>
    </div>
  );
}

export default Vegetables;
