import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

// Async thunk to fetch products from the API
export const fetchProducts = createAsyncThunk(
  "products/fetchProducts", // This can be any unique string for action type
  async () => {
    const response = await axios.get("http://localhost:8000/api/products/");
    return response.data;
  }
);

// Initial state of the products
const initialState = {
  products: [],
  status: "idle", // Tracks the loading, succeeded, failed state
  error: null,
};

// Creating the slice with reducers for loading, success, and error states
const productsSlice = createSlice({
  name: "products", // You can keep this 'products' as it's related to products
  initialState,
  extraReducers: (builder) => {
    builder
      .addCase(fetchProducts.pending, (state) => {
        state.status = "loading"; // Set loading state when request is pending
      })
      .addCase(fetchProducts.fulfilled, (state, action) => {
        state.status = "succeeded"; // Set succeeded when data is fetched
        state.products = action.payload; // Store products in state
      })
      .addCase(fetchProducts.rejected, (state, action) => {
        state.status = "failed"; // Set failed if there's an error
        state.error = action.error.message; // Store error message
      });
  },
});

export default productsSlice.reducer; // Export the reducer to be used in store
