import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { fetchProducts } from "..//..//ProductSlice/ProductsSlice";
import Loader from "..//../common/Loader";

function SmartStoreOverview() {
  const dispatch = useDispatch();

  const {
    items: products,
    status,
    error,
  } = useSelector((state) => state.products);

  useEffect(() => {
    if (status === "idle") {
      dispatch(fetchProducts());
    }
  }, [status, dispatch]);

  if (status === "loading") return <Loader />;

  if (status === "failed")
    return (
      <div className="text-red-500 text-center mt-4">
        Error: {error || "Failed to fetch products"}
      </div>
    );

  return (
    <section className="p-6 bg-gray-100 dark:bg-gray-900">
      <h1 className="text-3xl font-bold mb-4 text-center text-gray-800 dark:text-gray-200">
        Smart Store Overview
      </h1>

      <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {products.map((product) => (
          <div
            key={product.id}
            className="bg-white dark:bg-gray-800 shadow-md rounded-lg p-4 hover:shadow-lg transition duration-300"
          >
            <img
              src={product.image}
              alt={product.name}
              className="w-full h-48 object-cover rounded-md"
            />
            <h2 className="text-xl font-semibold mt-4 text-gray-800 dark:text-gray-100">
              {product.name}
            </h2>
            <p className="text-gray-600 dark:text-gray-400">
              {product.description}
            </p>
            <p className="text-green-600 font-bold mt-2">${product.price}</p>
            <button className="mt-4 bg-green-500 text-white px-4 py-2 rounded-full hover:bg-green-600 transition">
              Add to Cart
            </button>
          </div>
        ))}
      </div>
    </section>
  );
}

export default SmartStoreOverview;
