import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  fetchProductsAPI1,
  fetchProductsAPI2,
} from "..//../ProductSlice/smartStoreSlice";

const SmartStoreOverview = () => {
  const dispatch = useDispatch();
  const { itemsAPI1, itemsAPI2, statusAPI1, statusAPI2, errorAPI1, errorAPI2 } =
    useSelector((state) => state.smartStore);

  useEffect(() => {
    dispatch(fetchProductsAPI1());
    dispatch(fetchProductsAPI2());
  }, [dispatch]);

  return (
    <div>
      <h1>Smart Store Overview</h1>

      {/* Display API1 Products */}
      <div>
        <h2>API1 Products</h2>
        {statusAPI1 === "loading" && <p>Loading...</p>}
        {statusAPI1 === "failed" && <p>{errorAPI1}</p>}
        {statusAPI1 === "succeeded" && (
          <ul>
            {Array.isArray(itemsAPI1) ? (
              itemsAPI1.map((product) => (
                <li key={product.id}>{product.name}</li>
              ))
            ) : (
              <p>No products found</p>
            )}
          </ul>
        )}
      </div>

      {/* Display API2 Products */}
      <div>
        <h2>API2 Products</h2>
        {statusAPI2 === "loading" && <p>Loading...</p>}
        {statusAPI2 === "failed" && <p>{errorAPI2}</p>}
        {statusAPI2 === "succeeded" && (
          <ul>
            {Array.isArray(itemsAPI2) ? (
              itemsAPI2.map((product) => (
                <li key={product.id}>{product.name}</li>
              ))
            ) : (
              <p>No products found</p>
            )}
          </ul>
        )}
      </div>
    </div>
  );
};

export default SmartStoreOverview;
