import React from "react";
import Vegetable from "../../assets/Vegatables.jpg";
import Coffee from "../../assets/Coffee.jpg";
import Fruit from "../../assets/Fruite.jpg";
import Masalas from "../../assets/Masalas.jpg";
import Seeds from "../../assets/Seeds.jpg";
import dairy from "../../assets/Dairy.jpg";

function Menu() {
  const products = [
    { name: "Vegetables", img: Vegetable },
    { name: "Fruits", img: Fruit },
    { name: "Dairy", img: dairy },
    { name: "Seeds", img: Seeds },
    { name: "Masalas", img: Masalas },
    { name: "Coffee", img: Coffee },
  ];

  return (
    <div className="px-4 mt-10">
      {/* Header */}
      <div className="text-center">
        <h1 className="text-3xl font-extrabold mb-2">Menu</h1>
        <span className="text-lg text-gray-600">
          Choose the best Product from your favorite Farmer
        </span>
      </div>

      {/* Product Grid */}
      <div className="grid grid-cols-2 sm:grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-6 mt-10">
        {products.map((product, index) => (
          <div
            key={index}
            className="flex flex-col items-center text-center hover:bg-green-300 transition-all duration-300 border-2 border-black rounded-full p-2"
          >
            <div className="w-[150px] h-[150px] rounded-full overflow-hidden border-2 border-black">
              <img
                className="w-full h-full object-cover"
                src={product.img}
                alt={product.name}
              />
            </div>
            <span className="mt-3 font-semibold">{product.name}</span>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Menu;
