import React from "react";
import Slider from "react-slick";
import Farmer1 from "../../assets/FarmersSliding.jpg";

function Sliding() {
  const settings = {
    dots: true, // Show dots for navigation
    infinite: true, // Infinite loop for sliding
    speed: 500, // Speed of transition (in ms)
    slidesToShow: 1, // Show 1 slide at a time
    slidesToScroll: 1, // Scroll 1 slide at a time
    autoplay: true, // Enable autoplay
    autoplaySpeed: 2000, // Delay between slides (in ms, 2000 = 2 seconds)
    pauseOnHover: true, // Pause sliding when mouse hovers over the slider
  };

  return (
    <div className="relative">
      <Slider {...settings}>
        <div>
          <img
            className="h-[600px] w-full sm:w-[600px] md:w-[800px] lg:w-[1200px] xl:w-[1600px]"
            src={Farmer1}
            alt="farmer"
          />
        </div>
        <div>
          <img
            className="h-[600px] w-full sm:w-[600px] md:w-[800px] lg:w-[1200px] xl:w-[1600px]"
            src={Farmer1}
            alt="farmer"
          />
        </div>
        {/* Add more slides here */}
      </Slider>

      <div className="absolute bottom-10 left-1/2 transform -translate-x-1/2">
        <button className="text-center justify-center align-middle bg-green-800 h-[100px] w-[200px] rounded-full">
          Get Started
        </button>
      </div>
    </div>
  );
}

export default Sliding;
