import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { registerUser } from "../Authentication/AuthSlice";
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";

const Register = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { loading, error } = useSelector((state) => state.auth);

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [username, setUsername] = useState("");
  const [userType, setUserType] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!email || !password || !username || !userType) {
      alert("All fields are required!");
      return;
    }

    const userData = {
      email: email.trim(),
      password: password.trim(),
      username: username.trim(),
      user_type: userType,  // Ensure correct field name
    };

    console.log("Sending Data:", userData); // Debugging: Check in browser console

    try {
      const response = await dispatch(registerUser(userData)); // Dispatch registration action
      if (response.payload && !response.error) {
        // Navigate to login page only if registration was successful
        navigate("/login");
      }
    } catch (err) {
      console.error("Registration failed", err);
    }
  };

  return (
    <div className="flex justify-center items-center h-screen bg-gray-100">
      <div className="bg-white p-8 rounded-lg shadow-lg w-96">
        <h2 className="text-2xl font-bold text-center mb-4">Register</h2>
        {error && <div className="text-red-500 mb-4">{error}</div>}
        <form onSubmit={handleSubmit}>
          <input
            type="email"
            className="w-full p-2 mb-4 border border-gray-300 rounded"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="text"
            className="w-full p-2 mb-4 border border-gray-300 rounded"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="password"
            className="w-full p-2 mb-4 border border-gray-300 rounded"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <select
            className="w-full p-2 mb-4 border border-gray-300 rounded"
            value={userType}
            onChange={(e) => setUserType(e.target.value)}
            required
          >
            <option value="">Select User Type</option>
            <option value="farmer">Farmer</option>
            <option value="customer">Customer</option>
          </select>
          <button
            type="submit"
            className={`w-full py-2 ${
              loading ? "bg-gray-400" : "bg-blue-500"
            } text-white rounded`}
            disabled={loading}
          >
            {loading ? "Registering..." : "Register"}
          </button>
          <Link to="./Login.jsx" className="text-blue-500 text-center block mt-4">
            Already have an account? Login
          </Link>
        </form>
      </div>
    </div>
  );
};

export default Register;
